defmodule Day6 do
  def subroutine do
    ""
    |> String.codepoints()
    |> Enum.reduce_while([content: "", position: -1], fn x, acc ->
      [content: content, position: position] = acc
      length = String.length(content)

      cond do
        length == 4 -> {:halt, [content: content, position: position + 1]}
        String.contains?(content, x) -> {:cont, [content: Enum.at(String.split(content <> x, x, parts: 2), 1), position: position + 1]}
        true -> {:cont, [content: content <> x, position: position + 1]}
      end

    end)
  end
end

Day6.subroutine()
