defmodule Day6 do
  def subroutine do
    input = ""
    |> String.codepoints()
    |> Enum.reduce_while([content: "", position: -1], fn x, acc ->
      IO.inspect(acc)
      length = String.length(acc[:content])
      last_char = String.at(acc[:content], -1)
      if length == 4 do # change to 14 for part 2
        {:halt, [content: acc[:content], position: acc[:position] + 1]}
    else
        if String.contains?(acc[:content], x) do {:cont, [content: Enum.at(String.split(acc[:content] <> x, x, parts: 2), 1), position: acc[:position] + 1]} else {:cont, [content: acc[:content] <> x, position: acc[:position] + 1]} end
    end
    end)
  end
end

Day6.subroutine()
